<?php$report_id = $_REQUEST['report_id'];$query = "SELECT id,category,campaign_id,title,link,attachment,is_open,cn_en FROM ccms_report_manager WHERE id='$report_id';";$rslt=mysql_query($query, $link);$row=mysql_fetch_row($rslt);$attachments = $row[5];if(strlen($attachments)>0){	$html_att = "";	if (preg_match('/\-/', $attachments)) {			$attachments = explode("-",$attachments);		//$attachments = preg_match('/\-/', $attachments);		//$attachment = array_shift($attachments);				foreach($attachments as $att){			if($att != ""){			$html_att .= "<a href=\"upload/$row[2]/$att\">$att</a>&nbsp&nbsp[<a href=\"$PHP_SELF?ADD=20121123&id=$report_id&attachment=$att&file=upload/$row[2]/$att\">Delete</a>]</br>";			}		}	}else{		$html_att .= "<a href=\"upload/$row[2]/$attachments\">$attachments</a>&nbsp&nbsp[<a href=\"$PHP_SELF?ADD=20121123&id=$report_id&attachment=$attachments&file=upload/$row[2]/$attachments\">1Delete</a>]";	}}$id = $row[0];$category = $row[1];$campaign_id = $row[2];$title = $row[3];$url = $row[4];$is_open = $row[6];$cn_en = $row[7];$stmt = getCampaignSql($LOGuser_level,$LOGuser_name_id,'Y');$rslt=mysql_query($stmt, $link);$campaigns_to_print = mysql_num_rows($rslt);$o=0;while ($campaigns_to_print > $o) 	{	$row=mysql_fetch_row($rslt);	if($row[0] == $campaign_id){		$campaign_str .= "<option value=\"$row[0]\" selected=\"selected\">$row[1]</option>";	}else{		$campaign_str .= "<option value=\"$row[0]\">$row[1]</option>";	}	$o++;	}?><form name="form1" action=<?php echo $PHP_SELF;?> method="post" enctype="multipart/form-data"><table width="750" border="0" cellspacing="3" > <tbody> <tr class="tr_bg_color">		<td width='20%' align="right"><b>Type</b></td><td><b>Value</b></td></tr><tr bgcolor="#C2C2C2">			<td align="right"><b>Id:</b></td><td><input type="hidden" name="id" value="<?php echo $id;?>"><?php echo $id;?></td></tr><tr bgcolor="#C2C2C2">			<td align="right"><b>Category:</b></td><td><input type="text" name="ca